		if( strlen($_POST['doc']) < 1 ) {
			return array( 'error' => 'Введите номер телефона или номер договора!');
		}
		
		if(!empty((int)$_POST['rating'])){
			switch ((int)$_POST['rating']) {
				case 1:
				case 2:
				case 3:
				case 4:
				case 5:
					break;
				default:
					return array( 'error' => 'Мы зафиксировали попытку взлома!');
					break;
			}
		}else{
			return array( 'error' => 'Поставьте оценку!');
		}



		$result = $wpdb->insert( 'povestka_wp_company_rating', array(
			'user_id' => $current_user->ID,
			'org_id'  => (int)$_POST['org'],
			'comment' => remove_emoji($_POST['comment']), 
			'rating'  => (int)$_POST['rating'],
			'doc'     => $_POST['doc'],
			'status'     => 'new'
		), array('%d', '%d', '%s', '%d', '%s', '%s'));
		if( $result == false){
			return array( 'error' => 'Ошибка сохранения данных!');
		}elseif($result > 0){
			$_POST = array();
			return array( 'success' => 'Отзыв сохранён! После проверки отзыв будет опубликован на сайте');
		}else{
			return array( 'error' => 'Что-то пошло не так!');
		}
		
	}
	$event = wpshout_save_post_if_submitted();
